            return (
              <th>{result}</th>
          )
          })}


            </tr>
          </thead>
          <tbody>
          {this.state.data.map((result) => {
            return (
              <tr class="table-success">


                  <td>{result.urn}</td>
                  <td>{result.aspect}</td>
                  <td>{result.version}</td>
                  <td>{result.metadata}</td>
                  <td>{result.systemmetadata}</td>
                  <td>{result.createdon}</td>
                  <td>{result.createdby}</td>
                  <td>{result.createdfor}</td>
                </tr>
          )
          })}


          </tbody>
        </table>

        </div>
      </div>
  );
}
}
export default App;
